    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:\
        DefaultTableModel model = (DefaultTableModel) table1.getModel();
        model.setRowCount(0);
        try{
        Class.forName("com.mysql.cj.jdbc.Driver");
        Connection conn = DriverManager.getConnection("jdbc:mysql://localhost:3306/javaminiproj?" +"user=root");
            
            
            
            PreparedStatement pst=conn.prepareStatement("SELECT Name,Age,Symptoms FROM users WHERE City LIKE ? AND Status_at_present= '+ve'");
            String city = page3List.getSelectedItem().toString();
            pst.setString(1, city);
            
            ResultSet rs = pst.executeQuery();
            
            while(rs.next())
            {
                 String Name=rs.getString("Name");
                 String Age =rs.getString("Age");
                 String Symptoms=rs.getString("Symptoms"); 
                 //String author=rs.getString("author");
                
                 String tbData[]={Name, Age, Symptoms};
                 DefaultTableModel tblModel=(DefaultTableModel)table1.getModel();
                 
                 tblModel.addRow(tbData);
            }
        }
        catch(SQLException e)
        { 
            System.out.println(e);
            JOptionPane.showMessageDialog(this,"connection error");
        } catch (ClassNotFoundException ex) {
            Logger.getLogger(NewJFrame.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

